                 N = rotate_LeftRight(XZ); // Double rotation: Left(Z) then Right(X)
             else                           // Left Left Case
                 N = rotate_Right(XZ);     // Single rotation Right(X)
             // After rotation adapt parent link
         }
         else {
             if (BalanceFactor(X) > 0) {
                 BalanceFactor(X) = 0; // Z’s height increase is absorbed at X.
